mapper.select:
	select m.`id`,m.`gender`,m.`mobile`,m.`loginName`,m.`name`,m.`status`,m.`avatar`,m.`inviterUpperId`,m.`identityNo`,
	m.`inviterId`,m.`memberLevelId`,`l`.levelName as `memberLevelName`
	from `t_thinkingfruit_mall_member`as `m`
	left join `t_thinkingfruit_mall_member_level` as `l` on `l`.level=`m`.memberLevelId

	   1=1

	      and m.`loginName` like CONCAT('%',#{queryMap.loginName},'%')


       and m.`createTime` > #{queryMap.startTime} 


       and m.`createTime` < #{queryMap.endTime} 


	      and m.`memberLevelId` =#{queryMap.memberLevelId}

	   and `m`.status =1

select m.`id`,m.`mobile`,m.`loginName`,m.`status`,m.`inviterUpperId`, m.`inviterId`,m.`createTime` from `t_thinkingfruit_mall_member`as `m`
1=1
and m.`loginName` like CONCAT('%',#{queryMap.loginName},'%')
and m.`createTime` > #{queryMap.startTime}
and m.`createTime` < #{queryMap.endTime}
and `m`.status =0

select m.`id`,m.`mobile`,m.`loginName`,m.`upStatus`,m.`inviterUpperId`, m.`inviterId`,m.`createTime` from `t_thinkingfruit_mall_member`as `m`
1=1
and m.`loginName` like CONCAT('%',#{queryMap.loginName},'%')
and m.`createTime` > #{queryMap.startTime}
and m.`createTime` < #{queryMap.endTime}
and `m`.upStatus=1
