mapper.select:
select count(`id`) from `t_thinkingfruit_mall_user`
`nickname` like CONCAT(CONCAT('%',#{queryMap.nicknameStr}),'%')
and `loginName` like CONCAT(CONCAT(#{queryMap.loginNameStr},'%'),'%')
and `createTime` >= #{queryMap.startTime}
and `createTime` <= #{queryMap.endTime}
and `status` = 0

select t.`id` as id, t.`pswd` as pswd, t.`loginName` as loginName, t.`nickname` as nickname, CASE t.`sex` WHEN '0' THEN '男' WHEN '1' THEN '女' ELSE '' END as sexStr, t.`provName` as provName, t.`cityName` as cityName, t.`townName` as townName, t.`roleId` as roleId, t.`salt` as salt, t.`mobile` as mobile, t.`email` as email, t.`createTime` as createTime, t.`loginOutTime` as loginOutTime, t.`lastTime` as lastTime, t.`status` as status, t.`cityId`, t.`provinceId`, t1.`name` as roleName from `t_thinkingfruit_mall_user` t left join `t_thinkingfruit_mall_role` t1 on t1.`id` = t.`roleId`
t.`nickname` like CONCAT('%',CONCAT(#{queryMap.nicknameStr},'%'))
and t.`loginName` like CONCAT('%',CONCAT(#{queryMap.loginNameStr},'%'))
and t.`createTime` >= #{queryMap.startTime}
and t.`createTime` <= #{queryMap.endTime}
and t.`status` = 0
limit #{pagination.start},#{pagination.pageSize}
